var JSZip = require('jszip');
var fs = require('fs');
var cheerio = require('cheerio');

fs.readFile("ex.xlsx", function (err, data) {
  if (err) throw err;
  var zip = new JSZip();
  zip.load(data);
  var theme = zip.file('xl/styles.xml').asText();
  var $theme = cheerio.load(theme, null, null,{xmlMode: true});
});
